{
let sensors = toscalar(
    database('Global').RegistryStreamsPivot_MV
    | where Workflow contains "AcidTanks"
    | summarize make_list(sensor_id)
);
database('Global').FCTS_CURRENT_VALUE()
| where sensor_id in (sensors)
| project site_code, data_source, sensor_id, timestamp, value
