    let min_start_date_time = toscalar (T | summarize min(start_date_time));
    let max_end_date_time = toscalar (T | summarize max(end_date_time));
    let sensor_R1 = strcat(site_code, '-FMM200:W_001');
    let sensor_R2 = strcat(site_code, '-FMM300:W_001');
    database(site_name).FCTS
    | where sensor_id in~ (sensor_R1, sensor_R2 ) and timestamp between (min_start_date_time .. max_end_date_time)
    | project value = todecimal(value), sensor_name = case (
    sensor_id == sensor_R1, "R1",
    sensor_id == sensor_R2, "R2",
    sensor_id
    ), sensor_id, timestamp
    | join kind=rightouter T on $left.sensor_name == $right.id
    | where timestamp between (start_date_time .. end_date_time)
    | summarize total_value = round(avg(value),2) by total_batch_id,id,calculated_year
    | project total_batch_id, id, calculated_year, total_value
}

