{
    //Change History
    //Date				Change by			Description
    //2025-01-22		Samuel Xavier		Initial
    //2025-06-04        Putu Ananda         Make series in one hour interval

    let end_date_time2 = min_of(end_date_time, bin(now(), 15m));

    let latest_value_before_start = database(site_name).FCTS_DEDUP
        | where sensor_id == strcat(site_code, '-FFH:A_240') and timestamp between ((start_date_time - 30d) .. start_date_time)
        | summarize arg_max(timestamp, value)
        | project timestamp = start_date_time, value = round(todecimal(value), 2);

    let in_range_values = database(site_name).FCTS_DEDUP 
        | where sensor_id == strcat(site_code, '-FFH:A_240') and timestamp between (start_date_time .. end_date_time2)
        | project timestamp, value = round(todecimal(value), 2);

    let combined = union latest_value_before_start, in_range_values
        | distinct timestamp, value
        | sort by timestamp asc;

    combined
        | make-series value = max(value)               
                    default = real(null)             
                    on timestamp                     
                    from start_date_time to end_date_time2+15m
                    step 15m                          
        | extend value = series_fill_forward(value)    
        | extend timestamp = range(start_date_time, end_date_time2, 15m)   
        | mv-expand timestamp, value                   
        | project timestamp, value
}

