{
    // TimeEq Function
    // Returns the total time (in seconds) that a sensor matched the target value within
    // the provided time window.
    // TimeEq(site, sensor, start, end, target_value)
    /////////////////////////////////////////////////////
    // site = ADX Database Site Code
    // sensor = Sensor ID of interest
    // start = Lookup window start time
    // end = Lookup window end time
    // target_value = Target sensor value to filter on
    //////////////////////////////////////////////////////
    // Query the raw sensor data
    database(site).FCTS
    | where timestamp between (start .. end)
    and sensor_id == sensor
    // order ascending
    | order by timestamp asc
    // calculate the delta time between the next value and this value
    | extend time_in_value = next(timestamp) - timestamp
    // filter for only values that match the target
    | where value == target_value
    // calculate the sum of time in the target value
    | summarize total_time_in_value = floor(sum(time_in_value),1s) by sensor_id
    // convert the sum of time into total seconds
    | extend seconds_in_value = round(total_time_in_value / 1s, 0)
    | project timestamp=now(), sensor_id, total_time_in_value, seconds_in_value
}
